import React, { Component } from 'react';

class ItemDetails extends Component {
    render() {
        console.log(this.props.itemData);
        return (
            <div>
                <div className='row'>
                    <div className="col-xs-12 ">
                        <img
                            src={this.props.itemData.imageUrl}
                            className="img-responsive img" />
                    </div>
                </div>
                <div className="row">
                    <div className="col-xs-12">
                        <h1>{this.props.itemData.itemName}</h1>
                    </div>
                </div>
                <div className="row">
                    <div className="col-xs-12">
                         <p className="list-group-item-text">{ this.props.itemData.itemDetails }</p>
                    </div>
                </div>

                {this.props.itemData && Object.keys(this.props.itemData).length >0 ? <div className="row">
                    <div className="col-xs-12">
                        <ul className="list-group">
                         {this.props.itemData.itemIngridents.map((item,index) =>{
                             return(
                                  <li className="list-group-item">{item}</li>
                             )
                         })}

                        </ul>
                    </div>
                </div> :''
                }
                {this.props.itemData.itemName ? <div className="row">
                    <div className="col-xs-6">
                        <input type="button" value="Add" className="btn-group-lg btn-group-sm btn-group-xs btn-primary" id="btnAdd"
                        />
                    </div>
                </div> : ''
                }
            </div>
        )
    }
}

export default ItemDetails
